@namespace url(http://www.w3.org/1999/xhtml);
/* *** Basic *** */

*{
   background-color: transparent !important;
   color: #bcbcbc !important;
}

body, div, form, table, tbody, tr, td, fieldset, ul, li, a,
#content, #bodyContent{
   background-color: #333 !important;
   background-image: none !important;
}


/* *** Form Elements *** */
input, select, button, checkbox{
   -moz-appearance: none !important;
   border: 1px white solid !important;
   outline: none !important;
   background-color: #333 !important;
}

#simpleSearch{
   border: none !important;
}


/* *** Main *** */
#content{
   font-family: trebuchet, arial !important; 
   font-size: 12pt !important;
   border: 0 !important;
}

div#siteNotice{
   display:none !important;
}
p{
   border: none !important;
}
a{
   color: lightgrey !important;
}
a:visited{
   font-weight: bold !important;
   opacity: 0.65 !important;
}
a:link{
   color: white !important;
}

h2{
   border-bottom: #ccc 1px solid !important;
   font-size: 120% !important;
}
h2 > span{
   color: white !important;
}
h2 > span > span{
   color: white !important;
   font-size: 100% !important;
}

fieldset{
   border-color: #ddd !important;
}

#bodyContent ul{
   list-style-image: none !important;
   list-style-type: disc !important;
}
#toc ul{
   list-style-type: none !important;
}

.thumb{
   border-color: #333 !important;
}


/* *** Tabs *** */
#searchButton{
   display: none !important;
}

div.vectorTabs,
div.vectorTabs ul,
div.vectorTabs ul > li,
div.vectorTabs ul > li > a{
   background-color: #333 !important;
   background-image: none !important;
   border: none !important;
}

#right-navigation > .vectorMenu,
#ca-watch{
   display: none !important;
}

div.vectorTabs ul > li > a > span{
   padding: 0 !important;
}
div.vectorTabs ul > li > a{
  outline: 1px #888 solid !important;
  margin: 0 0.5em;
  height: auto !important;
  font-weight: normal !important;
}
div.vectorTabs ul > li > a:hover{
   text-decoration: none !important;
   outline: 1px #ccc solid !important;
}


/* *** Preference *** */
#preftoc > li{
   outline: 1px #ccc solid !important;
   border-bottom: 5px #ccc solid !important;
}

.successbox{
   border-color: white !important;
}


/* *** Search *** */
#search .search-types ul,
#search .results-info ul{
   list-style-type: none !important;
}


/* *** Logo Replacement *** */
#p-logo a {
   background: url(http://upload.wikimedia.org/wikipedia/en/b/bc/Wiki.png) no-repeat !important;
   position: absolute !important;
   clip: rect(0, 135px, 115px, 0) !important;
   height: 115px !important;
   overflow: hidden !important;
   margin-top: 30px !important;
}


/* *** Image and Math *** */
img{
   background-color: #fdfdfd !important;
}
img.tex{
   padding: 5px !important;
}
span.tex{
   border: 1px white solid !important;
   padding: 5px !important;
}

/* Remove stuff */
  .mfe-img-ct-inner.actions-buttons, .rpTbl th, .pd-data-tbl th, #user-feedback, #shd, #pushdownAdFragment, #cover_wrapper {
    display: none !important;
  }

  /* Remove background */
  html body, .asqFooter, .asqHeader, a.btn.btn-s.btn-ter, #spn_formSave, .stage[data-layout="grid"] .feed {
    background-image: none !important;
  }
  h1, h2, h3, h4, p, #gh-gb, .mfe-expand-card, .itemTooltip, .rsp-c, #CenterPanel, #CenterPanelInner, #vFoot, #MessageContainer div, .pageTab-pageLevelTabListContainer, .list-bulk-actions, .no-rows, .bgclr-fcfcfc, .mfe-reco, .visual-category.item-description, .content-empty, .expHeader.expHighlight, div .footer, #cover_image, .item_info {
    background: none !important;
  }
  
/* Backgrounds */
  .middle, footer, .navigation-desktop-flyout, .navigation-desktop-with-flyout.active, .c-std .w2b, .box, #itmSellerDesc, .asqHeader, .fol-olay, .sh-tbl th, .sub, .colw-tooltip, #gh-sbc-o, .gh-shop-active button#gh-shop-a, #VRFooterDF, #MessageContainer, .vip span, .row-header-inner, #regTab[aria-expanded="false"], #sgnTab[aria-expanded="false"], .mcd, .sm-co.sm-in, a.btn.btn-s.btn-ter, .dropdown-menu, #my-ebay-popover, .note-body, .watchlist-r.my-row-r.checked, .sel_info_row, #nav-bar, #nav-bar .nav-bar-inner .carousel .viewport, #nav-bar .nav-bar-inner .nav-title-wrap, #nav-bar .nav-bar-inner .carousel .overview li .nav-el, #nav-bar .nav-bar-inner .carousel .buttons.disable, .salesOrderTop, .alert-info, #cntwitemSummary_v4-2, #RTbubble_1, div.follow-dropdown-list, a.follow-dropdown-link.clicked, .tooltip, body #footerFragment, #index_card > .shad, .oly.bub {
